$(function() {
    var uluru = {lat: 50.216471, lng: 30.651175};
    var map = new google.maps.Map(document.getElementById('map'), {
      zoom: 10,
      center: uluru
    });
    var marker = new google.maps.Marker({
      position: uluru,
      map: map
    });


    // anchor scroll smooth and margin-top add
    $(function() {
    $('a[href*="#"]:not([href="#"])').click(function() {
      if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
        if (target.length) {
          $('html, body').animate({
            scrollTop: target.offset().top - 50
          }, 1000);
          return false;
        }
      }
    });
    });


    $('.js-open-full').on('click',function(){
      var index = $(this).data('index');
      $('.js-modal-photo[data-index=' + index + ']').toggleClass('hide');


    });

    $('.js-close-modal').on('click' , function(){
      var index = $(this).data('index');
      $('.js-modal-photo[data-index=' + index + ']').addClass('hide');
    });

    $('.js-next-photo').on('click' , function(){
      var index = $(this).data('index');
      $('.js-modal-photo[data-index=' + index + ']').addClass('hide');
      var index = index + 1;
      if(index == 32 || index > 32){
          index = 1;
      }
      $('.js-modal-photo[data-index=' + index + ']').removeClass('hide');
    });

    $('.js-prev-photo').on('click' , function(){
        var index = $(this).data('index');
        $('.js-modal-photo[data-index=' + index + ']').addClass('hide');
        var index = index - 1;
        if(index == 0 || index < 1){
            index = 32;
        }
        $('.js-modal-photo[data-index=' + index + ']').removeClass('hide');
    });



    $('.js-form').submit(function() {
        var nameLength = $('.js-name').val().length;
        var emailLength = $('.js-email').val().length;
        var numberLength = $('.js-number-input').val().length;
        //console.log(nameLength + "  -  " + emailLength + '  . ' + numberLength);
        if(numberLength < 4){
            console.log('Email incorrect');
        }
        if (nameLength < 1 || emailLength < 4 || numberLength < 4) {
            alert('Для отпавки формы коректно заполните все требуемые поля. (Ваше имя, email и Номер Телефона)');
            return false;
        }
    });









  //scrollreveal 
  window.sr = ScrollReveal({
    mobile: false,
    easing: 'cubic-bezier(0.6, 0.2, 0.1, 1)',
    duration: 1500
  });

  sr.reveal('.js-right', {
    duration: 2000,
    distance: '400px',
    origin: 'right'
  });

  sr.reveal('.js-left', {
    duration: 2000,
    distance: '400px',
    origin: 'left'
  });

  sr.reveal('.js-box', { duration: 500 }, 300);
  sr.reveal('.js-block');



   	$('.js-slick').slick({
         centerMode: true,
          centerPadding: '60px',
          dots: true,
	 	slidesToShow: 2,
	 	//slidesToScroll: 1,
	 	autoplay: true,
         cssEase:'linear',
	 	autoplaySpeed: 2000,
         //adaptiveHeight: false,
        prevArrow:'<button type="button" class="hvoinui__slider-arrow hvoinui__slider-arrow_previous"></button>',
         nextArrow:'<button type="button" class="hvoinui__slider-arrow hvoinui__slider-arrow_next"></button>'
	 });
});